import { 
  IonApp,
  IonButton,
  IonContent,
  IonHeader,
  IonIcon, 
  IonTitle, 
  IonToast, 
  IonToolbar,
  IonCard,
  IonCardHeader,
  IonCardTitle
} from '@ionic/react';
import React, {useState} from 'react';
import { play as playIcon } from 'ionicons/icons';

function App() {
  const [showToast, setShowToast] = useState(false);
  const handleClick = () => {
    setShowToast(true);
    setTimeout(() => setShowToast(false), 2000)
  };
  return (
    <IonApp>
      <IonHeader>
        <IonToolbar color="primary">

          <IonTitle><img src="https://localshops.com/packs/media/images/logo-42cd78b2.png" width="100px" alt="logo"/></IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
        <IonButton onClick={handleClick}>
          <IonIcon icon={playIcon} slot="start"/>
          Click Me!</IonButton>
        <IonToast isOpen={showToast} message="Hello World"/>
        <IonCard>
          <img src="https://images.unsplash.com/photo-1525610553991-2bede1a236e2?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1050&q=80" alt="Fruits"/>
          <IonCardHeader>
            <IonCardTitle>Shop, Discover, and Buy Everything Locally</IonCardTitle>
            </IonCardHeader>
        </IonCard>
      </IonContent>
    </IonApp>
  );
}

export default App;
