import { 
  IonApp,
  IonButton,
  IonContent,
  IonHeader,
  IonIcon, 
  IonTitle, 
  IonToast, 
  IonToolbar,
  IonList,
  IonItem,
  IonMenu,
  IonRouterOutlet,
  IonCard,
  IonCardHeader,
  IonCardTitle
} from '@ionic/react';
import React, {useState} from 'react';
import { play as playIcon } from 'ionicons/icons';

function App() {
  const [showToast, setShowToast] = useState(false);
  const handleClick = () => {
    setShowToast(true);
    setTimeout(() => setShowToast(false), 2000)
  };
  return (
    <IonApp>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Local Shops</IonTitle>
        </IonToolbar>
      </IonHeader>
      <IonContent className="ion-padding">
        <IonButton onClick={handleClick}>
          <IonIcon icon={playIcon} slot="start"/>
          Click Me!</IonButton>
        <IonToast isOpen={showToast} message="Hello World"/>
        <IonCard>
          <IonCardHeader>
            <IonCardTitle>Shop, Discover, and Buy Everything Locally</IonCardTitle>
            </IonCardHeader>
        </IonCard>
      </IonContent>
    </IonApp>
  );
}

export default App;
